            for (i=0; i < this.__nodes.length; ++i)
            {
                this.__nodes[i].destroy();

                if (this.__nodes[i].gluiobj &&
                    this.__nodes[i].gluiobj._internalGLObject.__objectRegistryId) {
                        this.__nodes[i].gluiobj._internalGLObject.destroy();
                }
            }
