function NgKVArrayToCmd(kvArray)
{
	var out = "";
	var count = 0;
	for (var s in kvArray)
	{
		var value = kvArray[s];
		
		out += "," + s + "," + Core.Base64.encode(value);
		++count;
	}
	return count + out;
}
